let data = msg.payload;
let tableData = [];

// Chuyển đổi dữ liệu Firebase thành định dạng bảng và lọc các giá trị UID null
for (let card in data) {
    if (data.hasOwnProperty(card)) {
        let entry = data[card].Entry || {};
        let exit = data[card].Exit || {};
        let cardInfo = data[card].CardInformation || {};
        let uid = data[card].UID;

        // Chỉ thêm vào tableData nếu UID không phải là null
        if (uid && uid !== "Null") {
            tableData.push({
                Card: card,
                Balance: data[card].Balance || "",
                CardType: cardInfo.CardType || "",
                BrandOfVehicle: cardInfo.BrandOfVehicle || "",
                Plate: cardInfo.Plate || "",
                TypeOfVehicle: cardInfo.TypeOfVehicle || "",
                EntryDate: entry.Date || "",
                EntryTime: entry.Time || "",
                ExitDate: exit.Date || "",
                ExitTime: exit.Time || "",
                IsVehicleIn: data[card].IsVehicleIn || false,
                UID: uid
            });
        }
    }
}

msg.payload = tableData;
flow.set('cards', tableData);
return msg;

