var currentTime = new Date().getTime();

// Kiểm tra nếu giá trị vượt ngưỡng
if (MQ2 > threshold) {
    if (startTime === null) {
        // Nếu đây là lần đầu vượt ngưỡng, lưu lại thời gian bắt đầu
        startTime = currentTime;
        context.set('startTime', startTime);
        return null;  // Không gửi thông báo ngay lập tức
    } else {
        // Nếu đã vượt ngưỡng trước đó, kiểm tra thời gian đã trôi qua
        var elapsedTime = currentTime - startTime;
        if (elapsedTime >= 3000) {  // 3000 ms = 3 giây
            msg.payload = "Khu C có cháy";
            context.set('notified', true);
            return msg;
        } else {
            // Chưa đủ 3 giây, không gửi thông báo
            return null;
        }
    }
} else {
    // Nếu giá trị dưới ngưỡng, reset trạng thái và thời gian
    context.set('startTime', null);
    context.set('notified', false);
    return null;
}

